import { createError } from "../composables/error.js";
const intervalError = "[nuxt] `setInterval` should not be used on the server. Consider wrapping it with an `onNuxtReady`, `onBeforeMount` or `onMounted` lifecycle hook, or ensure you only call it in the browser by checking `import.meta.client`.";
export const setInterval = import.meta.client ? window.setInterval : () => {
  if (import.meta.dev) {
    throw createError({
      statusCode: 500,
      message: intervalError
    });
  }
  console.error(intervalError);
};
// composables/useFaces.ts
import { ref, computed } from 'vue'

// –õ–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ (fallback)
const localFacesData = [
  {
    name: 'Olga Khayrullova',
    title: 'Personal Fashion Stylist',
    subtitle: 'Recognized by EFW SS26 Season',
    image: 'https://storage.yandexcloud.net/videos-meyou/efw2025/ai/faces/face-3.png',
    icon: true,
    order: 1,
    links: [
      { text: 'Olga Gallery 1', url: 'https://disk.yandex.ru/d/H1mkiuK2ZGnGtg' },
      { text: 'Olga Gallery 2', url: 'https://disk.yandex.ru/d/OyDVLRK3xh4XGA' },
      { text: 'Olga Gallery 3', url: 'https://disk.yandex.ru/d/i8UrwKqzfvP87Q' },
      { text: 'Olga Gallery 4', url: 'https://disk.yandex.ru/d/xcI2edSjve_VcQ' }
    ]
  },
  {
    name: 'Khalimova Liza',
    title: 'Designer',
    subtitle: 'Recognized by EFW SS26 Season',
    image: 'https://storage.yandexcloud.net/videos-meyou/efw2025/ai/faces/face-2.png',
    icon: true,
    order: 2,
    links: [
      { text: 'Liza Gallery', url: 'https://disk.yandex.ru/a/O-Yaq82vmplm-g' }
    ]
  },
  {
    name: 'Olga Baltrukevica',
    title: 'Model',
    subtitle: 'Recognized by EFW SS26 Season',
    image: 'https://storage.yandexcloud.net/videos-meyou/efw2025/ai/faces/face-1.png',
    icon: true,
    order: 3,
    links: [
      { text: 'Olga Gallery', url: 'https://failiem.lv/u/trcyc4awyu' }
    ]
  },
  {
    name: 'Ekaterina Potanina',
    title: 'Influencer',
    subtitle: 'Recognized by EFW SS26 Season',
    image: 'https://storage.yandexcloud.net/videos-meyou/efw2025/ai/faces/face-4.png',
    icon: false,
    order: 4,
    links: [
      { text: 'Instagram', url: 'https://www.instagram.com/potanina_ea/' }
    ]
  },
  {
    name: 'Katerina Melikhova',
    title: 'Guest',
    subtitle: 'Recognized by EFW SS26 Season',
    image: 'https://storage.yandexcloud.net/videos-meyou/efw2025/ai/faces/face-5.png',
    icon: false,
    order: 5,
    links: [
      { text: 'Instagram', url: 'https://www.instagram.com/katya_katerinina?igsh=YTZ5MnBodnN2dm5v' }
    ]
  }
]

// –†–µ–∞–∫—Ç–∏–≤–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
const facesData = ref([...localFacesData])
const isLoading = ref(false)
const isDataUpdated = ref(false)
const lastFetchTime = ref<number | null>(null)

// Google Sheets API URL –¥–ª—è EFW Faces
const GOOGLE_SHEETS_URL = 'https://docs.google.com/spreadsheets/d/1lK7QQ8McYRzsVlQ7I-xs_BLL0eVRhgKm6oVov8JJswE/gviz/tq?tqx=out:json&gid=0'

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–∏—è –¥–∞–Ω–Ω—ã—Ö –∏–∑ Google Sheets –≤ —Ñ–æ—Ä–º–∞—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
const transformFacesData = (rawData: any[]) => {
  return rawData
    .filter(row => row.name && row.image_url) // –§–∏–ª—å—Ç—Ä—É–µ–º –ø—É—Å—Ç—ã–µ —Å—Ç—Ä–æ–∫–∏
    .map(row => {
      const links = []

      // –°–æ–±–∏—Ä–∞–µ–º –≤—Å–µ —Å—Å—ã–ª–∫–∏ (–¥–æ 4 —à—Ç—É–∫)
      for (let i = 1; i <= 4; i++) {
        const textKey = `link_text_${i}` as keyof typeof row
        const urlKey = `link_url_${i}` as keyof typeof row

        if (row[textKey] && row[urlKey]) {
          links.push({
            text: row[textKey],
            url: row[urlKey]
          })
        }
      }

      return {
        name: row.name,
        title: row.title || '',
        subtitle: row.subtitle || '',
        image: row.image_url,
        icon: row.icon === 'TRUE' || row.icon === true,
        order: parseInt(row.order) || 999, // –ü–æ—Ä—è–¥–∫–æ–≤—ã–π –Ω–æ–º–µ—Ä, –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é 999
        links
      }
    })
    .sort((a, b) => a.order - b.order) // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –ø–æ—Ä—è–¥–∫—É
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –∏–∑ Google Sheets
export const fetchFaces = async () => {
  if (isLoading.value) return

  isLoading.value = true

  try {
    console.log('üîÑ –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ª–∏—Ü –∏–∑ Google Sheets...')

    const response = await fetch(GOOGLE_SHEETS_URL)

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`)
    }

    const text = await response.text()

    // –£–±–∏—Ä–∞–µ–º –ø—Ä–µ—Ñ–∏–∫—Å "google.visualization.Query.setResponse(" –∏ —Å—É—Ñ—Ñ–∏–∫—Å ");"
    const jsonText = text.replace(/^[^{]*/, '').replace(/[^}]*$/, '')

    const data = JSON.parse(jsonText)

    if (!data.table || !data.table.rows) {
      throw new Error('–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞–Ω–Ω—ã—Ö –∏–∑ Google Sheets')
    }

    // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ Google Sheets
    const headers = data.table.cols.map((col: any) => col.label)
    const rows = data.table.rows.map((row: any) => {
      const rowData: any = {}
      row.c.forEach((cell: any, index: number) => {
        rowData[headers[index]] = cell ? cell.v : ''
      })
      return rowData
    })

    console.log('üìä –ü–æ–ª—É—á–µ–Ω—ã –¥–∞–Ω–Ω—ã–µ –∏–∑ Google Sheets:', rows)

    // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ —Ñ–æ—Ä–º–∞—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
    const transformedData = transformFacesData(rows)

    if (transformedData.length > 0) {
      facesData.value = transformedData
      isDataUpdated.value = true
      lastFetchTime.value = Date.now()

      console.log('‚úÖ –î–∞–Ω–Ω—ã–µ –ª–∏—Ü —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω—ã –∏–∑ Google Sheets:', transformedData)
    } else {
      console.warn('‚ö†Ô∏è –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –¥–ª—è –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –∏–∑ Google Sheets')
    }

  } catch (error) {
    console.error('‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –ª–∏—Ü –∏–∑ Google Sheets:', error)
    // –û—Å—Ç–∞–≤–ª—è–µ–º –ª–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∫–∞–∫ fallback
  } finally {
    isLoading.value = false
  }
}

// Composable –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞—Ö
export const useFaces = () => {
  return {
    facesData: computed(() => facesData.value),
    isLoading: computed(() => isLoading.value),
    isDataUpdated: computed(() => isDataUpdated.value),
    lastFetchTime: computed(() => lastFetchTime.value),
    fetchFaces
  }
}
<template>
  <section class="md:mb-[76px] mb-[56px]">
    <div class="flex justify-between items-center mb-8 xl:mb-[75px]">
      <h2 id="faces-of-efw" data-nosnippet class="text-xl md:text-3xl xl:text-4xl md:text-left w-full text-center">Faces of Emirates Fashion Week</h2>
      <div class="flex items-center gap-4">
        <button 
          @click="handlePrev"
          class="w-6 h-6 md:w-10 md:h-10 flex items-center justify-center"
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M15 19L8 12L15 5" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
          </svg>
        </button>
        <button 
          @click="handleNext"
          class="w-6 h-6 md:w-10 md:h-10 flex items-center justify-center"
        >
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M9 5L16 12L9 19" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
          </svg>
        </button>
      </div>
    </div>

    <div class="overflow-x-auto hide-scrollbar md:px-0">
      <swiper
        :modules="[Navigation, Pagination, Autoplay]"
        :slides-per-view="1"
        :space-between="16"
        :navigation="false"
        :autoplay="{
          delay: 5000,
          disableOnInteraction: false
        }"
        :pagination="false"
        :breakpoints="{
          768: {
            slidesPerView: 2,
            spaceBetween: 30,
          },
          1024: {
            slidesPerView: 3,
            spaceBetween: 30,
          },
          1280: {
            slidesPerView: 4,
            spaceBetween: 30,
          }
        }"
        @swiper="onSwiper"
        @slideChange="onSlideChange"
        class="relative pb-12 px-4 md:px-0"
      >
        <swiper-slide v-for="(person, index) in facesData" :key="index">
          <div class="bg-white rounded-2xl h-full relative overflow-hidden">
            <img 
              :src="person.image" 
              :alt="person.name"
              class="w-full aspect-[4/5] rounded-lg object-cover"
              :loading="index === 0 ? 'eager' : 'lazy'"
            >
            <!-- –î–∏–∞–≥–æ–Ω–∞–ª—å–Ω–∞—è –ª–µ–Ω—Ç–æ—á–∫–∞ ICON of EFW -->
            <div v-if="person.icon" class="icon-ribbon">
              <span class="icon-ribbon__content">
                <svg class="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
                </svg>
                ICON of EFW
              </span>
            </div>
            <div class="p-4">
              <h3 class="text-lg font-semibold mb-1 text-center">{{ person.name }}</h3>
              <div v-if="person.title" class="text-sm text-gray-800 text-center">{{ person.title }}</div>
              <div v-if="person.subtitle" class="text-xs text-gray-500 text-center mb-4">{{ person.subtitle }}</div>
              <div class="flex flex-wrap gap-1 md:gap-2 justify-center">
                <a 
                  v-for="(link, linkIndex) in person.links" 
                  :key="linkIndex"
                  :href="link.url" 
                  target="_blank"
                  class="text-xs md:text-sm text-blue-600 hover:text-blue-800 underline"
                >
                  {{ link.text }}
                </a>
              </div>
            </div>
          </div>
        </swiper-slide>

      </swiper>
    </div>
  </section>
</template>

<script setup>
import { onMounted, onUnmounted, ref } from 'vue';
import { Swiper, SwiperSlide } from 'swiper/vue';
import { Navigation, Pagination, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { useFaces } from '~/composables/useFaces';

// –ò—Å–ø–æ–ª—å–∑—É–µ–º composable –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –¥–∞–Ω–Ω—ã–º–∏ –ª–∏—Ü
const { facesData, isLoading, isDataUpdated, fetchFaces } = useFaces();

const swiperInstance = ref(null);

const onSwiper = (swiper) => {
  swiperInstance.value = swiper;
};

const handlePrev = () => {
  swiperInstance.value?.slidePrev();
};

const handleNext = () => {
  swiperInstance.value?.slideNext();
};

const onSlideChange = () => {
  // –ú–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É –ø—Ä–∏ —Å–º–µ–Ω–µ —Å–ª–∞–π–¥–∞
};

// –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
onMounted(async () => {
  // –ü—ã—Ç–∞–µ–º—Å—è –æ–±–Ω–æ–≤–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ Google Sheets (—Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –µ—â–µ –Ω–µ –æ–±–Ω–æ–≤–ª–µ–Ω—ã)
  if (!isDataUpdated.value) {
    try {
      await fetchFaces();
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö –ª–∏—Ü:', error);
      // –ò—Å–ø–æ–ª—å–∑—É–µ–º –ª–æ–∫–∞–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∫–∞–∫ fallback
    }
  }
});
</script>

<style scoped>
:deep(.swiper) {
  padding-bottom: 40px !important;
  position: relative;
  overflow: hidden !important;
}

:deep(.swiper-wrapper) {
  align-items: stretch;
}

:deep(.swiper-slide) {
  height: auto;
}

/* –°—Ç–∏–ª–∏ –¥–ª—è –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */
:deep(.swiper-button-prev),
:deep(.swiper-button-next) {
  top: 50%;
  transform: translateY(-50%);
  display: flex !important;
}

:deep(.swiper-button-prev::after),
:deep(.swiper-button-next::after) {
  font-size: 20px;
}

@media (max-width: 767px) {
  button {
    display: none;
  }

  :deep(.swiper) {
    padding: 0 !important;
    overflow: visible !important;
  }

  :deep(.swiper-wrapper) {
    overflow: visible !important;
  }

  :deep(.swiper-slide) {
    opacity: 1;
    transition: opacity 0.3s ease;
  }

  :deep(.swiper-slide-next) {
    opacity: 0.6;
  }

  :deep(.swiper-slide-prev) {
    opacity: 0.6;
  }
}


button {
  transition: opacity 0.3s ease;
}

button:hover {
  opacity: 0.7;
}

button:active {
  opacity: 0.5;
}

/* –°–∫—Ä—ã—Ç–∏–µ —Å–∫—Ä–æ–ª–ª–±–∞—Ä–∞ */
.hide-scrollbar {
  -ms-overflow-style: none;  /* IE –∏ Edge */
  scrollbar-width: none;  /* Firefox */
}

.hide-scrollbar::-webkit-scrollbar {
  display: none; /* Chrome, Safari –∏ Opera */
}

/* –î–∏–∞–≥–æ–Ω–∞–ª—å–Ω–∞—è –ª–µ–Ω—Ç–æ—á–∫–∞ ICON of EFW */
.icon-ribbon {
  position: absolute;
  top: 0;
  left: 0;
  width: 0;
  height: 0;
}

.icon-ribbon__content {
  position: absolute;
  top: 65px; /* —Ä–∞–≤–µ–Ω 4 (tailwind top-4) */
  left: -25px; /* —Å–º–µ—â–µ–Ω–∏–µ, —á—Ç–æ–±—ã —Ü–µ–Ω—Ç—Ä–∏—Ä–æ–≤–∞–ª–æ—Å—å –ø–æ –¥–∏–∞–≥–æ–Ω–∞–ª–∏ */
  display: inline-flex;
  align-items: center;
  gap: 3px;
  padding: 4px 20px;
  background: #FBBF24; /* tailwind bg-yellow-400 */
  color: #111827; /* –ø–æ—á—Ç–∏ —á–µ—Ä–Ω—ã–π –¥–ª—è –∫–æ–Ω—Ç—Ä–∞—Å—Ç–∞ */
  font-weight: 700;
  font-size: 10px;
  white-space: nowrap; /* –Ω–µ –ø–µ—Ä–µ–Ω–æ—Å–∏—Ç—å —Ç–µ–∫—Å—Ç */
  line-height: 1;      /* –∫–æ–º–ø–∞–∫—Ç–Ω–∞—è –≤—ã—Å–æ—Ç–∞ —Å—Ç—Ä–æ–∫–∏ */
  border-radius: 0;
  box-shadow: 0 4px 10px rgba(0,0,0,0.15);
  transform: rotate(-45deg);
  transform-origin: left top;
}

@media (min-width: 768px) {
  /* .icon-ribbon__content {
    top: 18px;
    left: -56px;
    font-size: 13px;
    padding: 6px 14px;
  } */
}
</style>
<template>
  <section v-if="!loading && !error && faqData.length > 0" class="md:mb-[76px] mb-[56px]">
    <h2 class="text-2xl md:text-3xl font-bold text-left mb-8">FREQUENTLY ASKED QUESTIONS</h2>

    <!-- FAQ –∫–æ–Ω—Ç–µ–Ω—Ç -->
    <div class="flex flex-col gap-4">
      <div
        v-for="(faq, index) in faqData"
        :key="index"
      >
        <div 
          class="border border-black cursor-pointer"
          @click="toggleFaq(index)"
        >
          <div 
            class="w-full p-4 md:p-6 flex justify-between items-start hover:bg-gray-50"
            :class="{ 'border-b border-black': openFaqs.includes(index) }"
          >
            <span class="text-base md:text-xl xl:text-2xl pr-8 text-left">{{ faq.question }}</span>
            <span 
              class="text-2xl transform transition-transform duration-300 flex-shrink-0"
              :class="{ 'rotate-45': openFaqs.includes(index) }"
            >+</span>
          </div>

          <transition
            enter-active-class="transition duration-500 ease-out"
            enter-from-class="transform scale-y-95 -translate-y-1 opacity-0"
            enter-to-class="transform scale-y-100 translate-y-0 opacity-100"
            leave-active-class="transition duration-400 ease-in-out"
            leave-from-class="transform scale-y-100 translate-y-0 opacity-100"
            leave-to-class="transform scale-y-95 -translate-y-1 opacity-0"
          >
            <div 
              v-show="openFaqs.includes(index)"
              class="p-4 md:p-6 text-sm md:text-base xl:text-lg text-black-70 border-t border-black origin-top whitespace-pre-line"
              v-html="faq.answer"
            ></div>
          </transition>
        </div>
      </div>
    </div>

    <!-- WhatsApp –∫–Ω–æ–ø–∫–∞ –¥–ª—è –≤–æ–ø—Ä–æ—Å–æ–≤ -->
    <div class="mt-8 text-center">
      <WhatsAppButton 
        phone-number="https://wa.me/message/2F4O6ZT7YH5RG1"
        text="Ask questions on WhatsApp"
      />
    </div>
  </section>
</template>

<script setup>
import { ref, onMounted } from 'vue'
import WhatsAppButton from '@/components/ui/WhatsAppButton.vue'

const faqData = ref([])
const openFaqs = ref([])
const loading = ref(false)
const error = ref('')

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≥—Ä—É–∑–∫–∏ FAQ –∏–∑ Google Sheets
const fetchFaqData = async () => {
  loading.value = true
  error.value = ''

  try {
    const SPREADSHEET_ID = '1XCptY5cetlTjR3fMyt1dPAmEG6oKXpQMP8JsQGfHnIg'
    const CSV_URL = `https://docs.google.com/spreadsheets/d/${SPREADSHEET_ID}/export?format=csv&gid=0`

    const response = await fetch(CSV_URL)

    if (!response.ok) {
      throw new Error(`CSV export error: ${response.statusText}`)
    }

    const csvText = await response.text()

    // –ü–∞—Ä—Å–∏–º CSV –ø—Ä–∞–≤–∏–ª—å–Ω–æ (—É—á–∏—Ç—ã–≤–∞—è –º–Ω–æ–≥–æ—Å—Ç—Ä–æ—á–Ω—ã–µ —è—á–µ–π–∫–∏ –∏ –∫–∞–≤—ã—á–∫–∏)
    const lines = csvText.split('\n')
    const rows = []
    let currentRow = []
    let currentCell = ''
    let inQuotes = false
    let quoteCount = 0

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i]

      for (let j = 0; j < line.length; j++) {
        const char = line[j]

        if (char === '"') {
          quoteCount++
          inQuotes = quoteCount % 2 === 1
        } else if (char === ',' && !inQuotes) {
          currentRow.push(currentCell.trim())
          currentCell = ''
        } else {
          currentCell += char
        }
      }

      // –ï—Å–ª–∏ –º—ã –Ω–µ –≤ –∫–∞–≤—ã—á–∫–∞—Ö, –∑–∞–≤–µ—Ä—à–∞–µ–º —Å—Ç—Ä–æ–∫—É
      if (!inQuotes) {
        currentRow.push(currentCell.trim())
        if (currentRow.some(cell => cell.length > 0)) {
          rows.push(currentRow)
        }
        currentRow = []
        currentCell = ''
        quoteCount = 0
      } else {
        // –ï—Å–ª–∏ –≤ –∫–∞–≤—ã—á–∫–∞—Ö, –¥–æ–±–∞–≤–ª—è–µ–º –ø–µ—Ä–µ–Ω–æ—Å —Å—Ç—Ä–æ–∫–∏
        currentCell += '\n'
      }
    }

