    const isLocal = window.location.hostname.includes('localhost') ||
                   window.location.hostname.includes('127.0.0.1') ||
                   window.location.hostname.includes('0.0.0.0');

    if (isLocal) {
      // –õ–æ–∫–∞–ª—å–Ω–æ –∏—Å–ø–æ–ª—å–∑—É–µ–º API
      const response = await $fetch('/api/reels', {
        query: { _t: Date.now() } // –î–æ–±–∞–≤–ª—è–µ–º timestamp –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –∫–µ—à–∏—Ä–æ–≤–∞–Ω–∏—è
      });
      if (response.success) {
        // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ Instagram —Å—Å—ã–ª–∫–∏
        instagramReels.value = (response.videos || [])
          .filter(url => url.includes('instagram.com/reel/'))
          .filter((url, index, array) => array.indexOf(url) === index); // –£–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã

        console.log('Instagram Reels loaded from API:', instagramReels.value);
      }
    } else {
      // –ù–∞ –ø—Ä–æ–¥–∞–∫—à–µ–Ω–µ (GitHub Pages) –∏—Å–ø–æ–ª—å–∑—É–µ–º –ø—Ä—è–º—ã–µ –∑–∞–ø—Ä–æ—Å—ã –∫ Google Sheets
      const SPREADSHEET_ID = '12hBjIa1dMRgX7nVGBMvoAK_1d3Nxl4K-Gi0z9TIB4dQ';
      const CSV_URL = `https://docs.google.com/spreadsheets/d/${SPREADSHEET_ID}/export?format=csv&gid=0`;

      const response = await fetch(CSV_URL);

      if (!response.ok) {
        throw new Error(`CSV export error: ${response.statusText}`);
      }

      const csvText = await response.text();

      // –ü–∞—Ä—Å–∏–º CSV –ø—Ä–∞–≤–∏–ª—å–Ω–æ (—É—á–∏—Ç—ã–≤–∞—è –∑–∞–ø—è—Ç—ã–µ –≤ –∫–∞–≤—ã—á–∫–∞—Ö)
      const lines = csvText.split('\n');
      const rows = lines.filter(line => line.trim()).map(line => {
        const values = [];
        let current = '';
        let inQuotes = false;

        for (let i = 0; i < line.length; i++) {
          const char = line[i];

          if (char === '"') {
            inQuotes = !inQuotes;
          } else if (char === ',' && !inQuotes) {
            values.push(current.trim());
            current = '';
          } else {
            current += char;
          }
        }

        values.push(current.trim());
        return values;
      });

      // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ –º–∞—Å—Å–∏–≤ —Å—Å—ã–ª–æ–∫ –Ω–∞ Instagram Reels
      const allUrls = rows
        .map(row => row[0]?.trim()) // –ë–µ—Ä–µ–º –ø–µ—Ä–≤—É—é –∫–æ–ª–æ–Ω–∫—É
        .filter(url => url && url.startsWith('http')) // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –≤–∞–ª–∏–¥–Ω—ã–µ URL
        .map(url => url.replace(/^"(.*)"$/, '$1')) // –£–±–∏—Ä–∞–µ–º –∫–∞–≤—ã—á–∫–∏ –µ—Å–ª–∏ –µ—Å—Ç—å
        .filter((url, index, array) => array.indexOf(url) === index); // –£–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã

      // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ Instagram Reels
      instagramReels.value = allUrls.filter(url => url.includes('instagram.com/reel/'));
    }

    console.log('Instagram Reels loaded:', instagramReels.value);

    // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –æ–±–Ω–æ–≤–ª—è–µ–º embed –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö
    setTimeout(() => {
      if (window.instgrm && window.instgrm.Embeds) {
        console.log('Processing Instagram embeds...');
        window.instgrm.Embeds.process();
      } else {
        console.log('Instagram script not ready, retrying...');
        setTimeout(() => {
          if (window.instgrm && window.instgrm.Embeds) {
            window.instgrm.Embeds.process();
          }
        }, 1000);
      }
    }, 500);

  } catch (error) {
    console.error('Error fetching Instagram reels:', error);
    instagramReels.value = [];
  }
};

// –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è –º–æ–¥–∞–ª—å–Ω–æ–≥–æ –æ–∫–Ω–∞
const openImageModal = (imageSrc) => {
  selectedImage.value = imageSrc
}

const closeImageModal = () => {
  selectedImage.value = null
}

// –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è –∫–∞—Ä—É—Å–µ–ª—å—é Instagram Reels
const nextReel = () => {
  const maxIndex = Math.max(0, instagramReels.value.length - visibleReels.value)
  console.log('nextReel:', { currentReelIndex: currentReelIndex.value, maxIndex, visibleReels: visibleReels.value, totalReels: instagramReels.value.length })

  if (currentReelIndex.value < maxIndex) {
    currentReelIndex.value++
  } else {
    currentReelIndex.value = 0 // –ó–∞—Ü–∏–∫–ª–∏–≤–∞–µ–º –Ω–∞ –ø–µ—Ä–≤—ã–π —ç–ª–µ–º–µ–Ω—Ç
  }
  currentPage.value = Math.floor(currentReelIndex.value / visibleReels.value)
  console.log('nextReel result:', { currentReelIndex: currentReelIndex.value, currentPage: currentPage.value })
}

const prevReel = () => {
  const maxIndex = Math.max(0, instagramReels.value.length - visibleReels.value)
  if (currentReelIndex.value > 0) {
    currentReelIndex.value--
  } else {
    currentReelIndex.value = maxIndex // –ó–∞—Ü–∏–∫–ª–∏–≤–∞–µ–º –Ω–∞ –ø–æ—Å–ª–µ–¥–Ω–∏–π —ç–ª–µ–º–µ–Ω—Ç
  }
  currentPage.value = Math.floor(currentReelIndex.value / visibleReels.value)
}

const goToPage = (pageIndex) => {
  currentPage.value = pageIndex
  currentReelIndex.value = pageIndex * visibleReels.value
}

// –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è –∞–≤—Ç–æ–ø–ª–µ—è
const startAutoPlay = () => {
  if (instagramReels.value.length > visibleReels.value) {
    autoPlayInterval.value = setInterval(() => {
      nextReel()
    }, 5000) // 5 —Å–µ–∫—É–Ω–¥
  }
}

const stopAutoPlay = () => {
  if (autoPlayInterval.value) {
    clearInterval(autoPlayInterval.value)
    autoPlayInterval.value = null
  }
}



// Watcher –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ embed –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö
watch(instagramReels, async (newReels) => {
  if (newReels.length > 0) {
    await nextTick();
    setTimeout(() => {
      if (window.instgrm && window.instgrm.Embeds) {
        console.log('Processing Instagram embeds after data change...');
        window.instgrm.Embeds.process();
      }

    }, 100);

    // –ó–∞–ø—É—Å–∫–∞–µ–º –∞–≤—Ç–æ–ø–ª–µ–π –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏ –¥–∞–Ω–Ω—ã—Ö
    startAutoPlay();
  }
}, { deep: true });


// –ó–∞–≥—Ä—É–∂–∞–µ–º Instagram Reels –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
onMounted(() => {
  fetchInstagramReels();

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –≤–∏–¥–∏–º—ã—Ö Reels
  updateVisibleReels();

  // –î–æ–±–∞–≤–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
  window.addEventListener('resize', updateVisibleReels);


  // –ó–∞–≥—Ä—É–∂–∞–µ–º Instagram embed —Å–∫—Ä–∏–ø—Ç
  const script = document.createElement('script');
  script.src = '//www.instagram.com/embed.js';
  script.async = true;
  script.onload = () => {
    console.log('Instagram embed script loaded');
    // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –æ–±–Ω–æ–≤–ª—è–µ–º embed –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–∫—Ä–∏–ø—Ç–∞
    if (window.instgrm) {
      window.instgrm.Embeds.process();
    }

  };
  script.onerror = () => {
    console.error('Failed to load Instagram embed script');
  };
  document.head.appendChild(script);

});

// –û—á–∏—â–∞–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –ø—Ä–∏ —Ä–∞–∑–º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
onUnmounted(() => {
  // –£–¥–∞–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
  if (typeof window !== 'undefined') {
    window.removeEventListener('resize', updateVisibleReels);
  }

  // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∞–≤—Ç–æ–ø–ª–µ–π
  stopAutoPlay();
});
</script>

