//    URL 
export const getOptimizedImageUrl = (imageUrl) => {
  if (typeof window !== 'undefined' && window.innerWidth < 768) {
    return imageUrl.replace('.png', '-mobile.png');
  }
  return imageUrl;
};

//    
export const preloadImage = (imageUrl) => {
  const img = new Image();
  img.src = getOptimizedImageUrl(imageUrl);
  return img;
};

//     
export const preloadImages = (imageUrls) => {
  return imageUrls.map(preloadImage);
};

//        
export const getImageDimensions = () => {
  const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;
  return {
    width: isMobile ? 280 : 400,
    height: isMobile ? 420 : 600
  };
};

//       
export const getOptimizedImageAttributes = (imageUrl, alt = '') => {
  const dimensions = getImageDimensions();
  return {
    src: getOptimizedImageUrl(imageUrl),
    alt,
    loading: 'lazy',
    width: dimensions.width,
    height: dimensions.height,
    decoding: 'async'
  };
};

import { ref } from 'vue';
import gsap from 'gsap';

export function useNumberAnimation(initialValue = '0') {
  const displayNumber = ref(initialValue);

  const animateNumber = (startValue, endValue) => {
    const obj = { value: parseInt(startValue) };
    gsap.to(obj, {
      value: parseInt(endValue),
      duration: 2,
      ease: 'power1.out',
      onUpdate: () => {
        displayNumber.value = Math.round(obj.value).toString();
      },
    });
  };

  return {
    displayNumber,
    animateNumber,
  };
}
